import React from 'react';
import logo from '../images/vector.svg';
import { NavLink, useLocation } from 'react-router-dom';

function Header({ loggedIn, onSignOut, authUserEmail }) {

    const location = useLocation();
    const [menuIsOpen, setMenuIsOpen] = React.useState(false);

    function handleSignOut() {
        setMenuIsOpen(false);
        onSignOut();
    }

    return (
        <header 
        className={loggedIn ? 'header header_row-reverse' : 'header'}
        >
{loggedIn &&
        (
          <div
            className={menuIsOpen ? 'header__container header__container_opened' : 'header__container'}
          >
  
          </div>
        )
      }
      <div
        className="header__container-main"
      >
        <img className="header__logo" src={logo} alt="лого" />

        <address
              className="header__address"
            >
              {authUserEmail && authUserEmail}
            </address>

            <button
              className="header__button"
              type="button"
              onClick={handleSignOut}
            >
              Выйти
            </button>

        {!loggedIn &&
          (<nav>
            {location.pathname === '/sign-in' &&
              (
                <NavLink
                  className="header__nav"
                  to="/sign-up"
                >
                  Регистрация
                </NavLink>
              )
            }
            {location.pathname === '/sign-up' &&
              (
                <NavLink
                  className="header__nav"
                  to="/sign-in"
                >
                  Войти
                </NavLink>
              )
            }
          </nav>
        )
        }
      </div>

    </header>
  )
}

export default Header;
