import React from 'react';
import Sign from './Sign.js';

const Login = ({ onAuth, onCheckToken }) => {

    function handleSubmit(e) {
        e.prevent.default();
        onAuth(values);
    }

    return (
        <Sign
            title='Вход'
            buttonText='Войти'
            onSubmit={handleSubmit}
        >

            <input
                className="form__input form__input_theme_dark"
                type="email"
                id="login-email"
                name="email"
                required
                maxLength="30"
                value={values.email || ''}
                onChange={handleChange}
            />

            <input
                className="form__input form__input_theme_dark"
                type="password"
                id="login-pass"
                name="password"
                required
                value={values.email || ''}
                onChange={handleChange}
            />

        </Sign>

    )
}

export default Login;
