import React from 'react';
import Modal from 'react-modal';
import succesSign from '../images/succes-sign-up.svg';
import errorSign from '../images/error-sign-up.svg';

const InfoTooltip = (props) => {
    return (
        <Modal
            isOpen={props.isOpen}
            className="popup__container"
            overlayClassName="popup"
            onRequestClose={props.onClose}
        >
            <img
                className="popup__icon"
                src={props.isSucces ? succesSign : errorSign}
                alt={props.isSucces ? "успешно" : "ошибка"}
            />

            <h2
                className="popup__title"
            >
                {props.isSucces ? 'Вы успешно зарегистрировались!' : 'Что-то пошло не так! Пропробуйте ещё раз'}
            </h2>

            <button type="button" className='popup__close-button' onClick={props.onClose}></button>
        </Modal>
    )
}

export default InfoTooltip;
